import urllib.request
import json
import ssl
import certifi

def updateData():
    url = 'http://ddragon.leagueoflegends.com/cdn/12.23.1/data/en_US/champion.json' #patch specific!!!
    req = urllib.request.Request(url)
    response = urllib.request.urlopen(req,context=ssl.create_default_context(cafile=certifi.where()))
    cont = json.loads(response.read().decode('utf-8'))
    ch = cont['data']
    newNameList =[]
    newNameDict = {}
    newKeyDict = {}
    newIdToName = {}
    i=0
    for champion in ch:
        newNameList.append(ch[champion]['name'])
        newNameDict[ch[champion]['name']] = i
        newKeyDict[int(ch[champion]['key'])] = i
        newIdToName[str.lower(ch[champion]['id'])] = ch[champion]['name']
        i += 1

    print("nameList = " + str(newNameList))
    print("nameDict = " + str(newNameDict))
    print("keyDict = " + str(newKeyDict))
    print("idToName = " + str(newIdToName))

if __name__ == '__main__':
    updateData()

